import React, { useState } from "react";
import axios from "axios";
import "./QueryForm.css";

const QueryForm = () => {
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleQuery = async () => {
    setLoading(true);
    setError("");
    setResponse(null);

    try {
      const res = await axios.post("http://localhost:5001/chat", { query });
      setResponse(res.data);
    } catch (err) {
      setError("An error occurred while fetching data. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="query-form">
      <h1>Graduate Course Query</h1>
      <input
        type="text"
        placeholder="Enter your query (e.g., AI courses)"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleQuery} disabled={!query || loading}>
        {loading ? "Searching..." : "Submit"}
      </button>

      {error && <p className="error">{error}</p>}

      {response && (
        <div className="response-container">
          <h2>GPT's Response:</h2>
          <p>{response.gpt_response}</p>

          <h2>Courses:</h2>
          {response.courses && response.courses.length > 0 ? (
            <ul>
              {response.courses.map((course, index) => (
                <li key={index}>
                  <h3>
                    {course.catalog_number} - {course.course_name}
                  </h3>
                  <p><strong>Description:</strong> {course.description}</p>
                  <p><strong>Availability:</strong> {course.availability_status}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No courses found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default QueryForm;
