import React, { useState } from "react";
import axios from "axios";
import "./QueryForm.css";

const QueryForm = () => {
  const [query, setQuery] = useState("");
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleQuery = async () => {
    setLoading(true);
    setError("");
    setCourses([]);

    try {
      const res = await axios.post("http://localhost:5000/chat", { query });
      setCourses(res.data.courses);
    } catch (err) {
      setError("An error occurred while fetching data. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="query-form">
      <h1>Graduate Course Query</h1>
      <input
        type="text"
        placeholder="Enter your query (e.g., AI courses)"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleQuery} disabled={!query || loading}>
        {loading ? "Searching..." : "Submit"}
      </button>

      {error && <p className="error">{error}</p>}

      {courses.length > 0 && (
        <div className="response-container">
          <h2>Courses:</h2>
          <ul>
            {courses.map((course, index) => (
              <li key={index}>
                <h3>
                  {course.catalog_number} - {course.course_name}
                </h3>
                <p><strong>Description:</strong> {course.description}</p>
                <p><strong>Availability:</strong> {course.availability_status}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default QueryForm;
